sqlMap.select:
SELECT * FROM ( SELECT '' Processing_Flag, A.DATA_TYPE, A.PID, (SELECT RD_THEME FROM HP1DM01T WHERE PID=A.PID) PID_THEME, A.USE_COMPANY_CD, A.USE_ORG_CD, A.REQ_COMPANY_CD, A.REQ_ORG_CD, A.HPMS_ID, (SELECT HPMS_ID_NM_JP FROM HP1DM04T WHERE HPMS_ID =A.HPMS_ID) HPMS_NAME, A.INVEST_TYPE_CD, (SELECT INVEST_TYPE_NM FROM HP1DM07T WHERE INVEST_TYPE_CD = A.INVEST_TYPE_CD) INVESTMENT_TYPE_NAME, A.APPLICATION, A.ITEM_NAME, A.CUSTOMER_CD, A.UNIT, A.YYYYMM, VAL FROM HP2D001T A WHERE A.YYYYMM BETWEEN #FROM_YYYYMM# AND #TO_YYYYMM# AND ( A.USE_ORG_CD IN ( SELECT ORG_CD FROM HP1DM03T WHERE ORG_SEQ LIKE #ORG_SEQ# || '%' ) OR A.REQ_ORG_CD IN ( SELECT ORG_CD FROM HP1DM03T WHERE ORG_SEQ LIKE #ORG_SEQ# || '%' ) )
AND ( A.PID =#PID# or A.PID IN (SELECT SUB_PID FROM HP1DM02T WHERE PID = #PID#) ) AND A.HPMS_ID IN ( SELECT HPMS_ID FROM HP1DM04T WHERE HPMS_GRP_ID =#HPMS_GRP_ID# ) AND A.HPMS_ID =#HPMS_ID# AND A.DATA_TYPE =#DATA_TYPE# AND ( A.USE_COMPANY_CD =#COMPANY_CD# OR A.REQ_COMPANY_CD =#COMPANY_CD# )
) PIVOT ( SUM(VAL) FOR YYYYMM IN ( $YYYYMM_NULL$ ) ) ORDER BY DATA_TYPE, PID, USE_COMPANY_CD, USE_ORG_CD, REQ_COMPANY_CD, REQ_ORG_CD, HPMS_ID

SELECT * FROM ( SELECT '' Processing_Flag, A.DATA_TYPE, A.PID, (SELECT RD_THEME FROM HP1DM01T WHERE PID=A.PID) PID_THEME, A.USE_COMPANY_CD, A.USE_ORG_CD, A.REQ_COMPANY_CD, A.REQ_ORG_CD, A.HPMS_ID, (SELECT HPMS_ID_NM_JP FROM HP1DM04T WHERE HPMS_ID =A.HPMS_ID) HPMS_NAME, A.INVEST_TYPE_CD, (SELECT INVEST_TYPE_NM FROM HP1DM07T WHERE INVEST_TYPE_CD = A.INVEST_TYPE_CD) INVESTMENT_TYPE_NAME, A.APPLICATION, A.ITEM_NAME, A.CUSTOMER_CD, A.UNIT, A.YYYYMM, VAL FROM HP2D001T A WHERE A.YYYYMM BETWEEN #FROM_YYYYMM# AND #TO_YYYYMM#
AND ( A.PID =#PID# or A.PID IN (SELECT SUB_PID FROM HP1DM02T WHERE PID = #PID#) ) AND A.HPMS_ID IN ( SELECT HPMS_ID FROM HP1DM04T WHERE HPMS_GRP_ID =#HPMS_GRP_ID# ) AND A.HPMS_ID =#HPMS_ID# AND A.DATA_TYPE =#DATA_TYPE#
) PIVOT ( SUM(VAL) FOR YYYYMM IN ( $YYYYMM_NULL$ ) ) ORDER BY DATA_TYPE, PID, USE_COMPANY_CD, USE_ORG_CD, REQ_COMPANY_CD, REQ_ORG_CD, HPMS_ID
