            alert(" File Upload Fail");
            return;
            break;
    }

   // alert($('#'+file_Status).text() + " @@ "+ file_Status+ " @@" +$('#'+).val()+ "@@" +File_gubun );
    if($('#'+file_Status).val() ==""){

            return;
    }

    if ($("#C_CNT").val() != 1 ) {

		$('input[type=file]').val("");
        Return_RTN_MSG("00020");

        return ;

    }

     eval("FIleUPload_"+file_Status+"()");
