        $('input[type=file]').val("");
        ErrorCheck();
        return;
    }*/





}


function FileName_length(name) {
    var filename = $('#' + name).val();
    filename = filename.replace(/^.*[\\\/]/, '');
    var temp = filename.length;
    var tcount = 0;
    var onechar;

    for (k = 0; k < temp; k++) {
        onechar = filename.charCodeAt(k);
        if (onechar > 128) {
            tcount += 2;
        } else {
            tcount++;
        }
    }



    // alert( filename.length + "@@  "+ " @@ " + tcount);

    if (tcount > 64) {
        $('#' + name).val("");
        return true;
    } else {

        return false;
    }

}

