    if ($("#out_message").val() != "") {
        ErrorCheck();
        return;
    }
    if ($("#S_PID").val() == "") {
        Return_RTN_MSG("00003", "PID", "S_PID");
        return false;
    }
    //정규식 체크 
    if (PatternVal($("#S_PID").val(), "PID") == -1) {
        return;
    }
    if ($("#S_DCP_TYPE").val() == "") {
        Return_RTN_MSG("00004", "Data Type", "S_DCP_TYPE");
        return false;
    }
    //PID 존재여부 확인 
    PID_Confirm.submit(false, "dept2");

}

